<?phpclass service_IS_get_partners_contacts extends Service{	public function get_required_rights(){return array("see_information_session_details");}	public function input_documentation(){		?>		<ul>			<li>				<code>{array} partners_id</code>			</li>			<li>				<code>{boolean} no_address</code>			</li>			<li>				<code>{boolean} no_contact</code>			</li>		</ul>		<?php	}	public function output_documentation(){		?>		Return an array containing:		<ul>			<li><code>contacts</code> object from get_contacts functionfor each partner_id</li>		</ul>		<?php	}	public function documentation(){		echo "Get the partners contacts data, as used in select_partner.js";	}	public function execute(&$component,$input){		if(count($input["partners_id"]) > 0){			require_once("component/contact/service/get_contacts.inc");			$first = true;			echo "[";			foreach($input["partners_id"] as $partner_id){				if(!$first) echo ", ";				echo get_contacts("Organization_contact", "organization", "Organization_address", "organization",$partner_id,$input["no_address"],$input["no_contact"]);				$first = false;			}			echo "]";		} else echo "[]";	}}?>