<?phprequire_once("component/selection/SelectionJSON.inc");class service_exam_get_all_subject_names extends Service {		public function getRequiredRights() { return array("see_exam_subject"); }	public function documentation() {		echo "Get all the exam subject names";	}	public function inputDocumentation() {		?>		<ul>			<li><code>exclude_id</code> {number} (optional), id to exclude from the query</li>		</ul>		<?php	}	public function outputDocumentation() {		?>		<ul>			<li><code>false</code> if an error occured</li>			<li><code>names</code> {array} containing all the exam names</li>		</ul>		<?php	}		/**	 * @param $component selection	 * @see Service::execute()	 */	public function execute(&$component, $input) {		$names = null;		try {			if(isset($input["exclude_id"]))				$names = SelectionJSON::getAllExamSubjectNames($input["exclude_id"]);			else 				$names = SelectionJSON::getAllExamSubjectNames();		} catch(Exception $e) {			PNApplication::error($e);		}		if(PNApplication::hasErrors())			echo "false";		else			echo json_encode($names);	}	}?>