<?phprequire_once("component/selection/SelectionJSON.inc");class service_exam_get_all_subject_names extends Service {		public function get_required_rights() { return array(); }	public function documentation() {}	public function input_documentation() {}	public function output_documentation() {}		/**	 * @param $component selection	 * @see Service::execute()	 */	public function execute(&$component, $input) {		$names = null;		try {			if(isset($input["exclude_id"]))				$names = SelectionJSON::getAllExamSubjectNames($input["exclude_id"]);			else 				$names = SelectionJSON::getAllExamSubjectNames();		} catch(Exception $e) {			PNApplication::error($e);		}		if(PNApplication::has_errors())			echo "false";		else			echo json_encode($names);	}	}?>