<?phpclass service_get_partners_contacts extends Service{	public function get_required_rights(){return array();}	public function input_documentation(){		?>		<ul>			<li>				<code>{array} partners_id</code>			</li>			<li>				<code>{boolean} no_address</code>			</li>			<li>				<code>{boolean} no_contact</code>			</li>		</ul>		<?php	}	public function output_documentation(){}//TODO	public function documentation(){}//TODO	public function execute(&$component,$input){		if(count($input["partners_id"]) > 0){			require_once("component/contact/service/get_contacts.inc");			$first = true;			echo "[";			foreach($input["partners_id"] as $partner_id){				if(!$first) echo ", ";				echo get_contacts("Organization_contact", "organization", "Organization_address", "organization",$partner_id,$input["no_address"],$input["no_contact"]);				$first = false;			}			echo "]";		} else echo "[]";	}}?>